import { useState } from "react";
import { signUpUserWithEmailAndPassword } from "../utils/firebase";

const SignUp = () => {
    const defaultFormFields = {
        displayName:'',
        email: '',
        password: '',
        address: ''

    }
    const [formFields, setFormFields] = useState(defaultFormFields);
    const{displayName, email, password, address} = formFields;

    const onChangeHandler = (e) => {
        const {name, value} = e.target;
        setFormFields({...formFields, [name]:value});
        console.log(formFields);
    }
    const callSignUpFunction = async (e) => {
        e.preventDefault();
        await signUpUserWithEmailAndPassword(email, password);
    }

    
    return(
        <div>
            <form>
                <h2> your Email: </h2>
                <input placeholder="your email" type='email' value={email} name="email" onChange={onChangeHandler} />
                <h2> your Password : </h2>
                <input placeholder="password" type='password' value={password} name="password" onChange={onChangeHandler} />
                <h2> Your Display Name: </h2>
                <input placeholder="displayName" type="displayName" value={displayName} name="displayName" onChange={onChangeHandler} />
                <h2> Address : </h2>
                <input placeholder="address" type="address" value={address} name="address" onChange={onChangeHandler} />
                <button onClick={callSignUpFunction}> SignUp </button>
            </form>
            
        </div>
    )
}
export default SignUp;
